CREATE TABLE `channel_user` ( `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `channel_id` varchar(32) NOT NULL DEFAULT '', `uuid` varchar(32) NOT NULL DEFAULT '', `create_time` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`), KEY `idx_channel_id` (`channel_id`), KEY `idx_uuid` (`uuid`)) ENGINE = InnoDB CHARSET = utf8;
CREATE TABLE `channel_topic` ( `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `channel_id` varchar(32) NOT NULL DEFAULT '', `topic` varchar(32) NOT NULL DEFAULT '', `create_time` timestamp NOT NULL ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`), KEY `idx_channel_id` (`channel_id`), KEY `idx_topic` (`topic`) ) ENGINE = InnoDB CHARSET = utf8;
